import React from "react";
import { useState } from "react";
import UseForm from "../components/UseForm";

const Formulario = () => {

  const initialData = {
    nombre: '',
    email: '',
    asunto: '',
    mensaje: '',
  };


  const {form, loading, handleChange, handleSubmit} = useState(initialData);

  return (
    <div className="container mt-5">
        <h3>Formulario</h3>
      <form className="w-50">
        <label htmlFor="nombre" className="form-label mt-3">Nombre</label>
        <input type="text" name="nombre" className="form-control mb-3" />

        <label htmlFor="correo">Correo: </label>
        <input type="email" name="correo" className="form-control mb-3" />

        <label htmlFor="asunto">Asunto: </label>
        <input type="text" name="asunto" className="form-control mb-3" />

        <label htmlFor="mensaje">Mensaje: </label>
        <textarea name="mensaje" className="form-control "cols="30"rows="10">
        </textarea>

        <button type="submit" className="btn btn-success mt-5 w-100">Enviar</button>
      </form>
    </div>
  );
};

export default Formulario;
