import React from "react";
import UseForm from "../components/UseForm";

const Formulario = () => {

  const initialData = {
    nombre: '',
    correo: '',
    asunto: '',
    mensaje: '',
  };


  const onValidate = (form) => {
    let isError = false      //variable que se usara para saber si ocurrio un error
    let errors = {}          // objetos de errores que se retornaran

    if(!form.nombre.trim()){
      errors.nombre = "El campo no debe estar vacio"
      isError = true
    }

    return isError ? errors : null
  };

  const {form, errors,loading, handleChange, handleSubmit} = UseForm(initialData, onValidate);

  return (
    <div className="container mt-5">
        <h3>Formulario</h3>
      <form className="w-50" onSubmit={handleSubmit}>
        <label htmlFor="nombre" className="form-label mt-3">Nombre</label>
        <input type="text" name="nombre" className="form-control mb-3" value={form.nombre} onChange={handleChange}/>
        {errors.nombre && <div class="alert alert-danger p-2" role="alert">{errors.nombre}</div>}

        <label htmlFor="correo">Correo: </label>
        <input type="email" name="correo" className="form-control mb-3" value={form.correo} onChange={handleChange}/>
        {errors.correo && <div class="alert alert-danger p-2" role="alert">{errors.correo}</div>}

        <label htmlFor="asunto">Asunto: </label>
        <input type="text" name="asunto" className="form-control mb-3" value={form.asunto} onChange={handleChange}/>
        {errors.asunto && <div class="alert alert-danger p-2" role="alert">{errors.asusto}</div>}

        <label htmlFor="mensaje">Mensaje: </label>
        <textarea name="mensaje" className="form-control "cols="30"rows="10" value={form.mensaje} onChange={handleChange}>
        </textarea>
        {errors.mensaje && <div class="alert alert-danger p-2 mt-3" role="alert">{errors.mensaje}</div>}

        <button type="submit" className="btn btn-success mt-5 w-100">Enviar</button>
      </form>
    </div>
  );
};

export default Formulario;
